    plt.plot(sigmass[0], mut_ratess[i], color = graph_color)
    if i != 2:
        plt.annotate("Type "+str(i)+" G", xy=(sigmass[0][-1], mut_ratess[i][-1]), xytext=(sigmass[0][-1]+0.15, mut_ratess[i][-1]), size=7, va="center", annotation_clip=False)
